import React, { useState } from 'react';
import './Login.css';

const Login = () => {
  const [activeTab, setActiveTab] = useState('login');
  const [rememberMe, setRememberMe] = useState(false);

  return (
    <div className="login-container">
      <div className="login-card">
        {/* Logo */}
        <div className="logo-container">
          <div className="logo-circle">B</div>
          <div className="logo-text">Bookd</div>
        </div>

        {/* Welcome Text */}
        <div className="welcome-text">
          <h2>Welcome to Bookd</h2>
          <p>Please login to continue</p>
        </div>

        {/* Tabs */}
        <div className="tabs">
          <button
            className={`tab ${activeTab === 'login' ? 'active' : ''}`}
            onClick={() => setActiveTab('login')}
          >
            Login
          </button>
          <button
            className={`tab ${activeTab === 'register' ? 'active' : ''}`}
            onClick={() => setActiveTab('register')}
          >
            Register
          </button>
        </div>

        {/* Form */}
        {activeTab === 'login' ? (
          <div className="login-form">
            <div className="input-group">
              <span className="input-icon">‚úâÔ∏è</span>
              <input type="email" placeholder="Email" />
            </div>
            <div className="input-group">
              <span className="input-icon">üîí</span>
              <input type="password" placeholder="Password" />
            </div>

            <div className="form-options">
              <label className="remember-me">
                <input
                  type="checkbox"
                  checked={rememberMe}
                  onChange={() => setRememberMe(!rememberMe)}
                />
                Remember me
              </label>
              <a href="#" className="forgot-password">
                Forgot password?
              </a>
            </div>

            <button className="login-button">
              <span className="button-icon">‚Üí</span> Login
            </button>
          </div>
        ) : (
          <div className="register-form">
            {/* Register form would go here */}
            <p>Register form content</p>
          </div>
        )}

        {/* Footer */}
        <div className="footer">
          <a href="#">Terms of Service</a>
          <span>‚Ä¢</span>
          <a href="#">Privacy Policy</a>
        </div>

        {/* Tags */}
        <div className="tags">
          <span className="tag">Books</span>
          <span className="tag">Reading</span>
          <span className="tag">Community</span>
        </div>
      </div>
    </div>
  );
};

export default Login;
