import React, { useState } from 'react';
import './Login.css';

const Login = ({ onSwitchToRegister }) => {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    rememberMe: false
  });

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Login submitted:', formData);
    // Add your authentication logic here
  };

  return (
    <div className="login-container">
      <div className="login-card">
        <div className="logo">
          <div className="logo-circle">B</div>
        </div>
        
        <h1>Welcome to Bookd</h1>
        <p className="subtitle">Login or create an account to manage your scheduling journey</p>
        
        <div className="tabs">
          <button className="tab active">Login</button>
          <button className="tab" onClick={onSwitchToRegister}>Register</button>
        </div>
        
        <form className="login-form" onSubmit={handleSubmit}>
          <div className="input-group">
            <span className="input-icon">‚úâÔ∏è</span>
            <input
              type="email"
              name="email"
              placeholder="Email"
              value={formData.email}
              onChange={handleChange}
              required
            />
          </div>
          
          <div className="input-group">
            <span className="input-icon">üîí</span>
            <input
              type="password"
              name="password"
              placeholder="Password"
              value={formData.password}
              onChange={handleChange}
              required
            />
          </div>
          
          <div className="options">
            <label>
              <input
                type="checkbox"
                name="rememberMe"
                checked={formData.rememberMe}
                onChange={handleChange}
              />
              Remember me
            </label>
            <a href="#forgot-password" className="forgot-password">Forgot password?</a>
          </div>
          
          <button type="submit" className="login-button">
            <span className="button-icon">üîí</span> Login
          </button>
        </form>
        
        <p className="footer">
          By using Bookd, you agree to our <a href="#terms">Terms of Service</a> and <a href="#privacy">Privacy Policy</a>.
        </p>
        
        <div className="tags">
          <span>Secure login</span>
          <span>Data privacy</span>
          <span>Personalized scheduling</span>
        </div>
      </div>
    </div>
  );
};

export default Login;
