import React, { useState } from 'react';
import './InsuranceForm.css';

const InsuranceForm = () => {
  const [gender, setGender] = useState('male');
  const [name, setName] = useState('');
  const [dob, setDob] = useState('');
  const [mobile, setMobile] = useState('');
  const [whatsappUpdates, setWhatsappUpdates] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission
    console.log({ gender, name, dob, mobile, whatsappUpdates });
  };

  return (
    <div className="insurance-app">
      {/* Navbar */}
      <nav className="insurance-nav">
        <div className="nav-left">
          <span className="nav-logo">policybazaar</span>
          <span className="nav-website">www.policybazaar.com.au</span>
        </div>
        <div className="nav-right">
          <button className="nav-button">Claim Assistance</button>
          <button className="nav-button primary">Talk to Expert</button>
        </div>
      </nav>

      {/* Main Content */}
      <div className="insurance-container">
        <div className="insurance-header">
          <h1>‚Çπ1 Crore life cover starting from ‚Çπ485/month*</h1>
          <div className="discount-badges">
            <span>Get an Online Discount of 15%*</span>
            <span>PIP Promise Lowest Price Guarantee*</span>
          </div>
        </div>

        <form onSubmit={handleSubmit} className="insurance-form">
          <div className="form-section">
            <label>Gender</label>
            <div className="gender-toggle">
              <button
                type="button"
                className={`gender-btn ${gender === 'male' ? 'active' : ''}`}
                onClick={() => setGender('male')}
              >
                Male
              </button>
              <button
                type="button"
                className={`gender-btn ${gender === 'female' ? 'active' : ''}`}
                onClick={() => setGender('female')}
              >
                Female
              </button>
            </div>
          </div>

          <div className="form-section">
            <label>Your Name</label>
            <input
              type="text"
              placeholder="Enter Your Name"
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
            />
          </div>

          <div className="form-section">
            <label>Date of Birth</label>
            <input
              type="text"
              placeholder="DD/MM/YYYY"
              value={dob}
              onChange={(e) => setDob(e.target.value)}
              required
            />
          </div>

          <div className="form-section">
            <label>Mobile Number</label>
            <div className="mobile-input">
              <div className="country-selector">
                <span>India</span>
                <span>+91</span>
              </div>
              <input
                type="tel"
                placeholder="Enter Mobile Number"
                value={mobile}
                onChange={(e) => setMobile(e.target.value)}
                required
              />
            </div>
          </div>

          <button type="submit" className="view-plans-btn">View Plans</button>

          <div className="assistance-note">
            <p>Only certified Policybazaar expert will assist you</p>
          </div>

          <div className="form-footer">
            <label className="checkbox-label">
              <input
                type="checkbox"
                checked={whatsappUpdates}
                onChange={(e) => setWhatsappUpdates(e.target.checked)}
              />
              <span className="whatsapp-icon">üëç</span> Get updates on WhatsApp <span className="whatsapp-icon">üëç</span>
            </label>
            <p className="terms-text">
              By clicking, you agree to our Privacy policy, Terms of Use & * Disclaimer
            </p>
          </div>
        </form>

        <div className="stats-section">
          <h3>Policybazaar is one of India's leading digital insurance platform</h3>
          <div className="stats-grid">
            <div className="stat-item">
              <h4>9.7 crore</h4>
              <p>Registered Consumers</p>
            </div>
            <div className="stat-item">
              <h4>51</h4>
              <p>Insurance Partners</p>
            </div>
            <div className="stat-item">
              <h4>4.9 Crore</h4>
              <p>Policies Sold</p>
            </div>
          </div>
        </div>
      </div>

      {/* Footer */}
      <footer className="insurance-footer">
        <div className="footer-content">
          <p>¬© 2023 Policybazaar. All rights reserved.</p>
        </div>
      </footer>
    </div>
  );
};

export default InsuranceForm;
